mapper.update:
UPDATE training_course_application
SET dog_id = #{newDogId}, updated_at = NOW()
WHERE application_id = #{applicationId}

mapper.select:
SELECT user_id
FROM dog
WHERE dog_id = #{dogId}

SELECT COUNT(*) > 0
FROM training_course_application
WHERE dog_id = #{dogId}
AND session_id = #{sessionId}
AND status IN ('COUNSELING_REQUIRED','APPLIED','WAITING','ACCEPT')
  AND is_deleted = 0

SELECT max_students
FROM training_session
WHERE session_id = #{sessionId}

mapper.update:
UPDATE waiting
SET status = #{status}, updated_at = NOW()
WHERE application_id = #{applicationId}

UPDATE training_course_application
SET status = #{status},
updated_at = NOW()
WHERE application_id = #{applicationId}
AND status != #{status}

mapper.select:
SELECT session_id FROM training_session WHERE course_id IN
#{id}
AND is_deleted = 0

SELECT *
FROM training_course_application
WHERE created_by = #{userId}
AND session_id IN

    #{id}

AND is_deleted = 0

SELECT tca.* FROM training_course_application tca JOIN training_session ts ON tca.session_id = ts.session_id AND ts.is_deleted = 0 JOIN training_course tc ON ts.course_id = tc.course_id AND tc.is_deleted = 0 JOIN dog d ON tca.dog_id = d.dog_id AND d.is_deleted = 0 WHERE d.user_id = #{userId}
AND tca.dog_id = #{dogId}
AND tc.course_id IN
#{id}
AND tca.is_deleted = 0 AND tca.status NOT IN ('CANCELLED','EXPIRED','REFUNDED')

SELECT tca.* FROM training_course_application tca JOIN dog d ON tca.dog_id = d.dog_id AND d.is_deleted = 0 WHERE d.user_id = #{userId} AND tca.application_id IN
#{id}
AND tca.is_deleted = 0 AND tca.status IN ('APPLIED', 'WAITING', 'ACCEPT')

SELECT course_id
FROM wishlist_detail
WHERE wishlist_item_id = #{wishlistItemId}
